import React from 'react'
import Intro from '../intro/Intro'
import {gsap} from 'gsap'

import './css/landing.css'
import slideTop from '../../image/slide-top.jpg'
import slideBottom from '../../image/slide-bottom.jpg'


function LandingPage($){
    $(document).ready(function() {
        $(".enter").click(function() {
           unlock($(".enter"));
        });
     });

     function open(left, right, button) {
        gsap.to(left, 1.5, { width: 0 });
        gsap.to(right, 1.5, { width: 0 });
        gsap.to(button, 1.5, { "margin-left" : "-60px" , onComplete: function(){
           // Remove doors and button
           left.parent().remove();
           button.remove();
        }});
     }

     function unlock(el){
        gsap.to(el, 0.5, { transform: "rotate(90deg)", onComplete: function(){
           open($(".door .left"), $(".door .right"), el);
        }})
     }
    return(
        <div className="container">
            container
            <img className="slide-top" src={slideTop} alt="slide du plateau de jeu top"/>
            <Intro className="video"/>
            <img className="slide-bottom" src={slideBottom}  alt="slide du plateau de jeu bottom"/>
            <div class='enter'>Enter</div>
            <div class='door'>
                <div class='left'></div>
                <div class='right'></div>
            </div>
            <h1>Welcome</h1>
        </div>
    )
}

export default LandingPage
